import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image } from 'react-native';

const style = StyleSheet.create({
  imagem: {
    width: 100,
    height: 100,
    margin: 5,
  },
  box: {
    flexDirection: 'row',
    margin: 5,
    padding: 3
  },
  botao: {
    alignItems:'center',
    backgroundColor: 'none',
    margin: 3,
    borderWidth: 3,
    borderColor: '#000',
    borderRadius: 10
  },
});

export default function Home({ route, navigation }) {
  let user = route.params.nome;
  let estilo = { ...route.params.estilo, ...style };

  return (
    <View style={estilo.container}>
      <Text style={[estilo.texto, { color: '#000' }]}>Loja do {user}</Text>
      <View style={estilo.box}>
        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 10,
              produto: 'PlayStore',
              url: {
                uri:
                  'https://cdn.shopify.com/s/files/1/0788/5983/products/Copy_of_Google_Play_10_1024x1024.png',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://cdn.shopify.com/s/files/1/0788/5983/products/Copy_of_Google_Play_10_1024x1024.png',
            }}
            resizeMode="center"
          />
          <Text>PlayStore R$10</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 25,
              produto: 'PlayStore',
              url: {
                uri:
                  'https://cdn.shopify.com/s/files/1/0788/5983/products/Google_Play_25_2_250x250.png',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://cdn.shopify.com/s/files/1/0788/5983/products/Google_Play_25_2_250x250.png',
            }}
            resizeMode="center"
          />
          <Text>PlayStore R$25</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 30,
              produto: 'PlayStore',
              url: {
                uri:
                  'https://dfg.ai/itemimages/965498657-gift-card-google-play-57OV.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://dfg.ai/itemimages/965498657-gift-card-google-play-57OV.jpg',
            }}
            resizeMode="center"
          />
          <Text>PlayStore R$30</Text>
        </TouchableOpacity>
      </View>

      <View style={estilo.box}>
        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 20,
              produto: 'Steam',
              url: {
                uri:
                  'https://www.zero3games.com.br/loja/assets/0300_l.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://www.zero3games.com.br/loja/assets/0300_l.jpg',
            }}
            resizeMode="center"
          />
          <Text>Steam $20</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 50,
              produto: 'Steam',
              url: {
                uri:
                  'https://http2.mlstatic.com/D_NQ_NP_705590-MLB31864243798_082019-O.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://http2.mlstatic.com/D_NQ_NP_705590-MLB31864243798_082019-O.jpg',
            }}
            resizeMode="center"
          />
          <Text>Steam $50</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 100,
              produto: 'Steam',
              url: {
                uri:
                  'https://http2.mlstatic.com/D_NQ_NP_816669-MLB31209101533_062019-O.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://http2.mlstatic.com/D_NQ_NP_816669-MLB31209101533_062019-O.jpg',
            }}
            resizeMode="center"
          />
          <Text>Steam $100</Text>
        </TouchableOpacity>
      </View>

      <View style={estilo.box}>
        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 50,
              produto: 'League of Legends',
              url: {
                uri:
                  'https://cdn.awsli.com.br/600x450/553/553043/produto/29887119/878ee6f092.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://cdn.awsli.com.br/600x450/553/553043/produto/29887119/878ee6f092.jpg',
            }}
            resizeMode="center"
          />
          <Text>League of Legends R$50</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={estilo.botao}
          onPress={() =>
            navigation.navigate('Produto', {
              estilo,
              user,
              preco: 100,
              produto: 'League of Legends',
              url: {
                uri:
                  'https://cdn.awsli.com.br/600x450/754/754385/produto/51774496/0a61adcd2f.jpg',
              },
            })
          }
        >
          <Image
            style={estilo.imagem}
            source={{
              uri:
                'https://cdn.awsli.com.br/600x450/754/754385/produto/51774496/0a61adcd2f.jpg',
            }}
            resizeMode="center"
          />
          <Text>League of Legends R$100</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
