import React, { useState } from 'react';
import styled from 'styled-components/native';

const Page = styled.SafeAreaView`
  flex: 1;
  align-items: center;
`;

const Texto = styled.Text`
  font-size: 20px;
  color: #000;
`;

const Input = styled.TextInput`
  width: 90%;
  height: 50px;
  font-size: 18px;
  background-color: #eee;
  margin-top: 10px;
  border-radius: 10px;
  border: 3px solid;
  padding: 10px;
`;

const Botao = styled.TouchableOpacity`
  justify-content: center;
  align-items: center;
  margin: 5px;
  background-color: #41aef4;
  padding: 5px;
`;

export default function Login({ navigation }) {
  // criação das states
  // state do nome do usuario
  const [nome, setNome] = useState('trocar depois');
  // state de msg de login
  const [msg, setMsg] = useState('');

  function logar() {
    if (nome) {
      navigation.navigate('Home', { nome: nome });
    } else {
      setMsg('Insira um nome válido!');
    }
  }

  return (
    <Page>
      <Texto>{msg}</Texto>
      <Input
        placeholder="Nome de usuário"
        placeholderTextColor="#000"
        value={nome}
        onChangeText={(name) => setNome(name)}
      />
      <Botao onPress={logar(navigation)}>
        <Texto>Logar</Texto>
      </Botao>
    </Page>
  );
}
