import React, { useState } from 'react';
import { Image } from 'react-native';
import styled from 'styled-components/native';

const Pagina = styled.SafeAreaView`
  flex: 1;
  align-items: center;
`;

const Texto = styled.Text`
  font-size: 20px;
  margin-top: 10px;
`;

const Input = styled.TextInput`
  width: 90%;
  height: 50px;
  font-size: 18px;
  background-color: #eee;
  margin-top: 20px;
  border-radius: 10px;
  padding: 10px;
`;

const GerarButton = styled.Button`
  margin-top: 10px;
`;

let charset =
  'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@!#%.,';

export default function App() {
  // criação das states
  // state da senha que vai ser gerada
  const [psw, setPsw] = useState('');
  // state do tamanho da senha
  const [size, setSize] = useState('');

  function geraPsw() {
    let pass = '';
    let n = charset.length;

    if (size < 1 || size > 15) {
      alert('Tamanho da senha inválido');
    } else {
      // copia caracteres aleatórios para a senha
      for (let i = 0; i < size; i++) {
        pass += charset.charAt(Math.floor(Math.random() * n));
      }
      // atualiza o campo senha (psw)
      setPsw(pass);
    }
  }

  return (
    <Pagina>
      <Image
        source={require("./src/images/unicorn.png")}
        resizeMode='center'
      />
      <Texto>{size} Caracteres</Texto>
      <Input 
        placeholder="Entre 1 e 15" 
        palceholderTextColor="#000"
        keyboardType="numeric"
        value={size}
        onChangeText={(n) => setSize(n)}
        />
      <GerarButton title="Gerar Senha" onPress={geraPsw} />
      <Texto style={{fontSize:15}}>
        {psw}
      </Texto>
    </Pagina>
  );
}
