export default function Login({ navigation }) {
  // criação das states
  // state do nome do usuario
  const [nome, setNome] = useState('');
  // state de msg de login
  const [msg, setMsg] = useState('');

  function logar() {
    if (nome) {
      navigation.navigate('Home', { nome: nome });
    } else {
      setMsg('Insira um nome válido!');
    }
  }

  return (
    <View style={estilo.container}>
      <Text>{msg}</Text>
      <TextInput
        style={estilo.input}
        placeholder="Nome de usuário"
        onChangeText={(name) => setNome(name)}
      />
      <TouchableOpacity onPress={logar(navigation)}>
        <Text>Logar</Text>
      </TouchableOpacity>
    </View>
  );
}

const estilo = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  input: {
    width: '90%',
    height: 50,
    fontSize: 18,
    backgroundColor: '#eee',
    marginTop: 10,
    borderRadius: 10,
    border: [3, 'solid'],
    padding: 10,
  },
});
