import React, { useState } from 'react';
import {
  styleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from 'react-native';

export default function Login({ navigation }) {
  // criação das states
  // state do nome do usuario
  const [nome, setNome] = useState('trocar depois');
  // state de msg de login
  const [msg, setMsg] = useState('');

  function logar() {
    if (nome) {
      navigation.navigate('Home', { nome: nome });
    } else {
      setMsg('Insira um nome válido!');
    }
  }

  return (
    <View>
      <Text>{msg}</Text>
      <TextInput
        placeholder="Nome de usuário"
        placeholderTextColor="#000"
        value={nome}
        onChangeText={(name) => setNome(name)}
      />
      <TouchableOpacity onPress={logar(navigation)}>
        <Text>Logar</Text>
      </TouchableOpacity>
    </View>
  );
}
