import React, { useState } from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';

export default function Produto({ route, navigation }) {
  let estilo = { ...route.params.estilo, ...style };
  let url = route.params.url;
  let user = route.params.user;
  let preco = route.params.preco;
  let produto = route.params.produto;
  return (
    <View style={estilo.container}>
      <Text style={[estilo.texto, { color: '#000' }]}>Loja do {user}</Text>
      <View style={estilo.box}>
        <Image
          style={estilo.imagem}
          source={{uri: url.uri}}
          resizeMode="center"
        />
      </View>
        <Text style={[estilo.texto, { color: '#000' }]}>Produto: {produto}      Pre√ßo: R${preco}</Text>
    </View>
  );
}

const style = StyleSheet.create({
  imagem:{
    height: 300,
    with: 300
  }
})
