import React, { useState } from 'react';
import styled from 'styled-components/native';

const Pagina = styled.SafeAreaView`
  flex: 1;
  align-items: center;
`;

const Texto = styled.Text`
  font-size: 20px;
`;

const Result = styled.SafeAreaView`
  align-items: center;
  margin-top: 10px;
  width: 90%;
  padding: 5px;
  border: 1px solid;
  background-color: #eee;
`;

const Result2 = styled.SafeAreaView`
  align-items: center;
  margin-top: 10px;
  width: 90%;
  padding: 5px;
  border: 1px solid;
  background-image: linear-gradient(315deg, rgba(63,8,96,1) 0%, rgba(158,7,51,1) 98%);
`;

const Input = styled.TextInput`
  width: 90%;
  height: 50px;
  font-size: 18px;
  background-color: #eee;
  margin-top: 20px;
  border-radius: 10px;
  border: 1px solid;
  padding: 10px;
`;

const GerarButton = styled.TouchableOpacity`
  justify-content: center;
  align-items: center;
  margin: 10px;
  background-color: #41aef4;
  padding: 10px;
`;

const TextoBtn = styled.Text`
  color: #fff;
  font-size: 15;
`;

const Imagem = styled.Image`
  width: 200px;
  height: 200px;
  margin-top: 30px;
`;

let charset =
  'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@!#.,';

export default function App() {
  // criação das states
  // state da senha que vai ser gerada
  const [psw, setPsw] = useState('');
  // state do tamanho da senha
  const [size, setSize] = useState('');
  // state com o caminho da img usada e depois é trocada
  const [img, setImg] = useState({
    source: require('./src/images/unicornDormindo.png'),
  });
  const [model, setModel] = useState({ style: { display: 'none' } });

  function geraPsw() {
    let pass = '';
    let n = charset.length;

    if (size < 1 || size > 15) {
      alert('Tamanho da senha inválido');
      setImg({ source: require('./src/images/unicornDormindo.png') });
      setModel({ style: { color: 'red', display: 'block' } });
    } else {
      setImg({ source: require('./src/images/unicornPass.png') });
      setModel({ style: { color: 'red', display: 'none' } });
      // copia caracteres aleatórios para a senha
      for (let i = 0; i < size; i++) {
        pass += charset.charAt(Math.floor(Math.random() * n));
      }
      // atualiza o campo senha (psw)
      setPsw(pass);
    }
  }

  return (
    <Pagina>
      <Imagem source={img.source} resizeMode="center" />
      <Texto>{size} Caracteres</Texto>
      <Input
        placeholder="Tamanho da Senha (1 a 15)"
        palceholderTextColor="#000"
        keyboardType="numeric"
        value={size}
        onChangeText={(n) => setSize(n)}
      />
      <GerarButton onPress={geraPsw}>
        <TextoBtn>Gerar Senha</TextoBtn>
      </GerarButton>
      <Result>
        <Texto style={{ fontSize: 15 }}>{psw} </Texto>
      </Result>

      <Texto style={model.style}>teste</Texto>

      <Result2 style={{ marginTop: '70%' }}>
        <Texto style={{ fontSize: 15 }}>Ajax Lima</Texto>
      </Result2>
    </Pagina>
  );
}
