import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from 'react-native';

export default function Login({ navigation }) {
  // criação das states
  // state do nome do usuario
  const [nome, setNome] = useState('Trocar depois');
  // state de msg de login
  const [msg, setMsg] = useState('');

  function logar(navigation) {
    if (nome) {
      navigation.navigate('Home', { nome: nome, estilo: estilo });
    } else {
      setMsg('Insira um nome válido!');
    }
  }

  return (
    <View style={estilo.container}>
      <Text style={estilo.title}>{msg}</Text>
      <TextInput
        style={estilo.input}
        placeholder="Nome de usuário"
        value={nome}
        onChangeText={(nome) => setNome(nome)}
      />
      <TouchableOpacity style={estilo.botao} onPress={() => logar(navigation)}>
        <Text style={estilo.texto}>Logar</Text>
      </TouchableOpacity>
    </View>
  );
}

const estilo = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#000'
  },
  input: {
    width: '90%',
    height: 50,
    fontSize: 18,
    backgroundColor: '#eee',
    marginTop: 10,
    borderRadius: 10,
    borderWidth: 3,
    borderColor: 'purple',
    padding: 10
  },
  texto: {
    fontSize: 20,
    color: '#fff',
  },
  title: {
    fontSize: 30,
    color: '#000',
  },
  botao: {
    justifyContent: 'center',
    alignItems: 'center',
    margin: 5,
    backgroundColor: '#41AEF4',
    borderRadius: 5,
    padding: 5,
  },
});
