import React, { useState } from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import styled from 'styled-components/native';

const Pagina = styled.SafeAreaView`
  flex: 1;
  align-items: center;
`;

const Texto = styled.Text`
  font-size: 20px;
  margin-top:10px;
`;

const Input = styled.TextInput`
  width: 90%;
  height: 50px;
  font-size: 18px;
  background-color: #eee;
  margin-top: 20px;
  border-radius: 10px;
  padding: 10px;
`;

const GerarButton = styled.Button`
  margin-top:10px;
`;

let charset ='abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@!#%.,';

export default function App() {
  // criação das states
  // state da senha que vai ser gerada
  const [psw, setPsw] = useState('');
  // state do tamanho da senha
  const [size, setSize] = useState('');
  // quantidade de caracteres pedidos

  function geraPsw() {
    let pass = '';
    let n = charset.length;

    if (size < 1 || size > 15) {
      alert('Tamanho da senha inválido');
    } else {
      // copia caracteres aleatórios para a senha
      for (let i = 0; i < size; i++) {
        pass += charset.charAt(Math.floor(Math.random() * n));
      }
      // atualiza o campo senha (psw)
      setPsw(pass);
    }
  }

  return (
    <Pagina>
      <Texto>{size} Caracteres</Texto>
      <Input onChangeText={(n) => setSize(n)} />
      <Texto>{psw} {size}</Texto>
    </Pagina>
  );
}
