import React from "react";
import { Image } from "react-native";
import styled from "styled-components/native";

const Header = styled.View`
  flex-direction: row;
  background-color: #ddd;
  height: auto;
  flex-wrap: wrap;
  justify-content: center;
  padding-top: 1px;
`;

const Quadrado = styled.View`
  background-color: ${(props) => props.cor};
  width: 150px;
  height: 150px;
  justify-content: center;
  align-items: center;
  margin-left: 1px;
  border: 3px solid transparent;
  border-image: linear-gradient(
    to bottom right,
    #b827fc 0%,
    #2c90fc 25%,
    #b8fd33 50%,
    #fec837 75%,
    #fd1892 100%
  );
  border-image-slice: 1;
`;
const Pagina = styled.View`
  flex: 1;
  align-items: center;
  flex-direction: column;
`;

function App() {
  return (
    <Pagina>
      <Header>
        <Quadrado cor="green">
          <Image
            source={require("./src/imagens/uepa.png")}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="cover"
          />
        </Quadrado>
        <Quadrado cor="blue">
          <Image
            source={{
              uri:
                "https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg",
            }}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
      <Header>
        <Quadrado cor="green">
          <Image
            source={require("./src/imagens/uepa.png")}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="cover"
          />
        </Quadrado>
        <Quadrado cor="blue">
          <Image
            source={{
              uri:
                "https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg",
            }}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
      <Header>
        <Quadrado cor="purple" style={{ width: 300 }}>
          <Image
            source={{
              uri:
                "https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg",
            }}
            style={{ width: 250, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
    </Pagina>
  );
}

export default App;
