import React from "react";
import { Image } from "react-native";
import styled from "styled-components/native";

const Header = styled.View`
  flex-direction: row;
  background-color: #ddd;
  height: auto;
  flex-wrap: wrap;
  justify-content: center;
  padding-top:1px;
  `;

  const Quadrado = styled.View`
  background-color: ${(props) => props.cor};
  width: 150px;
  height: 150px;
  justify-content: center;
  align-items: center;
  margin-left:1px;
  border: 2px solid transparent;
  border: double 0.3em transparent;
  border-radius: 10px;
     /*added a colourstop here, without the third colourstop you get a hard edge*/
  background: linear-gradient(white, white), 
  repeating-linear-gradient(to right, rgb(82, 82, 209),rgb(235, 50, 235), rgb(82, 82, 209));
  background-origin: border-box;
  background-clip: content-box, border-box;


            animation-name: rainbow;
            animation-duration:  3s;

            /*set animation to continue forever, and to move at a single rate instead of easing*/
            animation-iteration-count: infinite;
            animation-timing-function: linear;
`;
const Pagina = styled.View`
  flex: 1;
  flex-direction: column;
`;

function App() {
  return (
    <Pagina>
      <Header>
        <Quadrado cor="green">
          <Image
            source={require("./src/imagens/uepa.png")}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="cover"
          />
        </Quadrado>
        <Quadrado cor="blue">
          <Image
            source={{uri:'https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg'}}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
      <Header>
        <Quadrado cor="green">
          <Image
            source={require("./src/imagens/uepa.png")}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="cover"
          />
        </Quadrado>
        <Quadrado cor="blue">
          <Image
            source={{uri:'https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg'}}
            style={{ width: 100, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
      <Header>
        <Quadrado cor='purple' style={{width:300}}>
          <Image
            source={{uri:'https://geeksinaction.com.br/wp-content/uploads/2020/06/Kumo-desu-ga-Nani-ka-light-novel-volume-4-artwork-cropped-destaque-752x440.jpg'}}
            style={{ width: 250, height: 100, backgroundColor: "#aaa" }}
            resizeMode="center"
          />
        </Quadrado>
      </Header>
    </Pagina>
  );
}

export default App;
